<?php

namespace Tests\Browser;

use Illuminate\Foundation\Testing\DatabaseMigrations;
use Laravel\Dusk\Browser;
use Tests\DuskTestCase;

class CreateSitemapTest extends DuskTestCase
{

    public function testCreateSitemapNormal(): void
    {
        $this->browse(function (Browser $browser) {
            $browser->visit('/links/create')
                ->type("categorie", "Golf")
                ->type("naam", "DuskGolf")
                ->type("functie", "GolfLeraar")
                ->type("verwijzing", "www.example.com")
                ->press('Voeg link toe')
                ->assertPathIs("/links")
                ->assertSee("Link opgeslagen.");
        });
    }
    public function testCreateSitemapLongCategorieText():void{
        $this->browse(function (Browser $browser) {
            $browser->visit('/links/create')
                ->type("categorie", "")
                ->type("naam", "DuskGolf")
                ->type("functie", "GolfLeraar")
                ->type("verwijzing", "www.example.com")
                ->press('Voeg link toe')
                ->assertPathIs("/links/create")
                ->assertSee("Categorie mag niet groter zijn dan 255 karakters.");
        });
    }
    public function testCreateSitemapLongNaamText():void{
        $this->browse(function (Browser $browser) {
            $browser->visit('/links/create')
                ->type("categorie", "DuskGolf")
                ->type("naam", "")
                ->type("functie", "GolfLeraar")
                ->type("verwijzing", "www.example.com")
                ->press('Voeg link toe')
                ->assertPathIs("/links/create")
                ->assertSee("Naam mag niet groter zijn dan 255 karakters.");
        });
    }
    public function testCreateSitemapLongFunctieText():void{
        $this->browse(function (Browser $browser) {
            $browser->visit('/links/create')
                ->type("categorie", "DuskGolf")
                ->type("naam", "DuskGolf")
                ->type("functie", "")
                ->type("verwijzing", "www.example.com")
                ->press('Voeg link toe')
                ->assertPathIs("/links/create")
                ->assertSee("Functie mag niet groter zijn dan 255 karakters.");
        });
    }
    public function testCreateSitemapLongVerwijzingText():void{
        $this->browse(function (Browser $browser) {
            $browser->visit('/links/create')
                ->type("categorie", "DuskGolf")
                ->type("naam", "GolfMens")
                ->type("functie", "GolfLeraar")
                ->type("verwijzing", "www..com")
                ->press('Voeg link toe')
                ->assertPathIs("/links/create")
                ->assertSee("Verwijzing mag niet groter zijn dan 255 karakters.");
        });
    }
    public function testCreateSitemapBackButton():void{
        $this->browse(function (Browser $browser) {
            $browser->visit('/links/create')
                ->click('Ga terug')
                ->assertPathIs("/links")
                ->assertDontSee("mag niet groter zijn dan 255 karakters.")
                ->assertDontSee("Link opgeslagen.");
        });
    }

    public function testDeleteSitemap():void{
        $this->browse(function (Browser $browser) {
            $browser->visit('/links')
                ->click('Verwijderen')
                ->assertPathIs("/links")
                ->assertSee("Link verwijderd.");
        });
    }
}
