<?php

namespace Tests\Browser;

use Illuminate\Foundation\Testing\DatabaseMigrations;
use Laravel\Dusk\Browser;
use Tests\DuskTestCase;

class EditSitemapTest extends DuskTestCase
{
    public function EditSuccessful(): void
    {
        $this->browse(function (Browser $browser) {
            $browser->visit('/links')
                    ->clickLink('Aanpassen')
                    ->type('categorie', 'GolfDB')
                    ->type('functie', 'Golf Specialist')
                    ->type('naam', 'Henk Hendrik')
                    ->type('verwijzing', 'https://www.nu.nl/')
                    ->press('Update')
                    ->assertSee('Evenement geupdatet')
                    ->assertPathIs('/events');
        });
    }

    public function EditRequired(): void
    {
        $this->browse(function (Browser $browser) {
            $browser->visit('/links')
                    ->clickLink('Aanpassen')
                    ->type('categorie', '')
                    ->type('functie', '')
                    ->type('naam', '')
                    ->type('verwijzing', '')
                    ->press('Update')
                    ->assertSee('categorie veld is verplicht')
                    ->assertSee('functie veld is verplicht')
                    ->assertSee('naam veld is verplicht')
                    ->assertSee('verwijzing veld is verplicht')
                    ->assertPathIs("/links/*/edit");
        });
    }

    public function testEditSitemapTooLongCategory(): void
    {
        $this->browse(function (Browser $browser) {
            $browser->visit('/links')
                    ->clickLink("Aanpassen")
                    ->type("categorie", "")
                    ->type('functie', 'Golf Specialist')
                    ->type('naam', 'Henk Hendrik')
                    ->type('verwijzing', 'https://www.nu.nl/')
                    ->press("Update")
                    ->assertPathIs("/links/*/edit")
                    ->assertSee("Categorie mag niet groter zijn dan 255 karakters.");
        });
    }

    public function testEditSitemapTooLongFunction(): void
    {
        $this->browse(function (Browser $browser) {
            $browser->visit('/links')
                    ->clickLink("Aanpassen")
                    ->type("categorie", "GolfDB")
                    ->type('functie', '')
                    ->type('naam', 'Henk Hendrik')
                    ->type('verwijzing', 'https://www.nu.nl/')
                    ->press("Update")
                    ->assertPathIs("/links/*/edit")
                    ->assertSee("Functie mag niet groter zijn dan 255 karakters.");
        });
    }

    public function testEditSitemapTooLongName(): void
    {
        $this->browse(function (Browser $browser) {
            $browser->visit('/links')
                    ->clickLink("Aanpassen")
                    ->type("categorie", "GolfDB")
                    ->type('functie', 'Golf Specialist')
                    ->type('naam', '')
                    ->type('verwijzing', 'https://www.nu.nl/')
                    ->press("Update")
                    ->assertPathIs("/links/*/edit")
                    ->assertSee("Naam mag niet groter zijn dan 255 karakters.");
        });
    }

    public function testEditSitemapTooLongLink(): void
    {
        $this->browse(function (Browser $browser) {
            $browser->visit('/links')
                    ->clickLink("Aanpassen")
                    ->type("categorie", "GolfDB")
                    ->type('functie', 'Golf Specialist')
                    ->type('naam', 'Henk Hendrik')
                    ->type('verwijzing', '')
                    ->press("Update")
                    ->assertPathIs("/links/*/edit")
                    ->assertSee("Verwijzing mag niet groter zijn dan 255 karakters.");
        });
    }

    
}
